package Data.Structures;


public class LinkedList {


    Node head=null;
    Node tail = null;


    public static void main(String[] args) {
        LinkedList l = new LinkedList();
        l.insert(5);
        l.insert(4);
        l.insert(3);

        l.toString();


    }

    public void  insert(int element){

        Node tmp = new Node();


        if (head==null){
            //CREATE OBJ FROM NODE CLASS
            //insert
            tmp.data=element;
            //head pointer to data
            head=tmp;
            //tail pointer to data
            tail=tmp;

        } else {
            //store
            tmp.data=element;
            //point to new node
            tail.next=tmp;
            tail=tmp;

        }

    }

    public boolean Includes(int d){
        Node current = this.head;

        while (current.next !=null){
            if (current.data==d){
                return true;

            }
            current=current.next;
        }
        return false;
    }

    public String toString() {
        String msg = " ";
        if (head==null) {
            return null;
        } else {
            while (head != null) {
                msg = msg + "{" + head.data + "}" + " -> ";
                head = head.next;
            }
            msg = msg + "NULL";
            System.out.print(msg);
            return msg;
        }
    }

    

    }


